import Carousel from 'react-material-ui-carousel';
import { Paper, Button } from '@material-ui/core';
import aljalil from './slider/aljalil_bg.jpg';
import alnoor from './slider/alnoor_bg1.jpg';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import { useHistory } from "react-router-dom";
function Item(props)
{
    let history = useHistory();
    return (
        <Paper >
            <img src = {props.item.image} style={{width:"100%",height:"100%",padding:"0"}} alt={props.item.content}></img>
            <h1 style = {{position:"absolute",top:"30%",left:"100px",color:"black",fontSize:"4rem",fontFamily:"'Cinzel',serif"}}>{props.item.heading}</h1>

            <Button className="CheckButton" onClick = {() => history.push(props.item.link )} style={{color:"white",background:"black",fontSize:"1rem",position:"relative",bottom:"50%",left:"0",minWidth:"20%",opacity:"0.8"}}>
                {props.item.content}
            </Button>
        </Paper>
    )
}

function SlideShow(){
  
        var items = [
            {
               image: aljalil,
               content: "Explore Al-Jalil Garden",
               link : '/aljalil',
               heading: "Al-Jalil Garden"

            }, {
                image: alnoor,
                content: "Explore Al-Noor Garden",
                link : "/alnoor",
                heading: "Al-Noor Garden"
             },
           
        ]
    
        return (
            <Carousel
                autoPlay = {true}
                interval = {222000}
                animation = "fade"
                swipe = {true}
                
                navButtonsAlwaysVisible = {false}
                fullHeightHover={false}
                indicators = {false}     
                navButtonsProps={{          
                    style: {
                        buttonWrapper: {
                            position: "absolute",
                            height: "100px",
                            backgroundColor: "black",
                            top: "calc(50% - 70px)",
                            '&:hover': {
                                '& $button': {
                                    backgroundColor: "black",
                                    filter: "brightness(120%)",
                                    opacity: "0"
                                }
                            }
                        },
                        fullHeightHoverWrapper: {
                            height: "100%",
                            top: "0"
                        },
                        buttonVisible:{
                            opacity: "1"
                        },
                        buttonHidden:{
                            opacity: "0",
                        },
                        button: {
                            margin: "0 10px",
                            position: "relative",
                            backgroundColor: "black",
                            top: "calc(50% - 20px) !important",
                            color: "white",
                            fontSize: "30px",
                            transition: "200ms",
                            cursor: "pointer",
                            '&:hover': {
                                opacity: "1 !important"
                            },
                        },
                        // Applies to the "next" button wrapper
                        next: {
                            right: 0
                        },
                        // Applies to the "prev" button wrapper
                        prev: {
                            left: 0
                        },
                    }    
                }} 
            >
                {
                    items.map( (item, i) => <Item key={i} item={item} /> )
                }
            </Carousel>
        )
    }

export default SlideShow;
